import React, {useState, useEffect} from 'react'
import {useLocation} from 'react-router-dom';
import axios from '../../api/axios';

export default function SearchPage() {
    const [searchResults, setSearchResults] = useState([]); // 검색 결과 담을 state

    const useQuery = () => {
        return new URLSearchParams(useLocation().search);
    };

    let query = useQuery();
    const searchTerm = query.get("q");

    useEffect(() => { //searchTerm state가 업데이트 되면 실행
      if (searchTerm) { //검색하려는 값이 존재하면
        fetchSearchMovie(searchTerm);
      }
    }, [searchTerm])

    const fetchSearchMovie  = async (searchTerm) => { //searchTerm에 대한 영화 정보를 요청
        try {
            const request = await axios.get(`/search/multi?include_adult=false&query=${searchTerm}`)
            setSearchResults(request.data.results) //가져온 영화 정보 searchResults에 저장, 정보 없으면 에러 출력
        } catch (e) {
            console.log("error", e)
        }
    }

    //검색 결과 존재 유무에 따라서 다른 JSX 반환
    const renderSearchResults = () =>{
        return searchResults.length > 0 ? (
            <section className='search-container'>
                {}

            </section>
        ): (<></>);
    }

  return (  
    <div>SearchPage</div>
  )
}
