import React, { useEffect } from "react";
import { useParams, useState } from "react-router-dom";
import axios from "../../api/axios";

export default function DetailePage() {

  // 파라미터 값을 URL을 통해서 넘겨받은 페이지에서 사용할 수 있게 해줌.
  //여기선 movieId(App.js의 <Route path=":movieId" element={<DetailePage/>}/>)
  //useParams()는 객체로 반환되기 때문에 여러 개의 값이 안에 들어있을 수 있음.

  const { movieId } = useParams();

  const [movies, setMovies] = useState({});

  useEffect(() => {
    async function fetchData() {
      const request = await axios.get(
        `https://api.themoviedb.org/3/movie/${movieId}`
      );
      setMovies(request.data);
    }
    fetchData();
  }, [movieId]);

  return <div>DetailePage</div>;
}
