import SearchPage from "./pages/SearchPage";

const Layout = () => {
  return (
    <div>
      <Nav />

      <Outlet />

      <Footer />
    </div>
  );
};

function App() {
  return (
    <div className="App">
      <Routes>
        <Route path="/" element={<Layout />}>
        <Route index element={<MainPage />}/>
        <Route path=":movieId" element={<DetailPage  />}/>
        <Route path="searchpage" element={<SearchPage />}/>

        </Route>
      </Routes>
    </div>
  );
}

export default App;
